package bean;

public class Point {
	public float x;
	public float y;

	public Point() {
		// TODO Auto-generated constructor stub
	}

	public Point(float x, float y) {
		super();
		this.x = x;
		this.y = y;
	}

	public float getX() {
		return x;
	}

	public void setX(float x) {
		this.x = x;
	}

	public float getY() {
		return y;
	}

	public void setY(float y) {
		this.y = y;
	}

}